package ex01;

public class printTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String verb = "현실이 된다";
		System.out.println("생각이 " + verb);
		
		String s = "1234567";
		System.out.println("문자열의 길이는 " + s.length());
		
		String s1 = "ABCDEFG";
		s1 = s1.toLowerCase();
		System.out.println(s1);
		
		System.out.println("2 + 3 = "+ (2 + 3));
		
		System.out.println("2 + 3 = " + 2 + 3);
	}

}
